mapper.select:
select
  a_id, a_r_id, a_ua_id, a_operating_id, a_application_status, a_date
from union.application


        and a_id = #{aId}


        and a_r_id = #{aRId}


        and a_ua_id = #{aUaId}


        and a_operating_id = #{aOperatingId}


        and a_application_status = #{aApplicationStatus}


        and a_date = #{aDate}
